import React from 'react';

import HotelService from "~/packages/TravelgoOne/services/HotelService";

const createSitemap = (models) => `<?xml version="1.0" encoding="UTF-8"?>
    <sitemapindex xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
      <sitemap>
        <loc>${process.env.app_url}/sitemap/accommodations</loc>
        <lastmod>2021-02-22T13:02:52+00:00</lastmod>
      </sitemap>
      <sitemap>
        <loc>${process.env.app_url}/sitemap/post-sitemap.xml</loc>
        <lastmod>2021-03-10T09:19:13+00:00</lastmod>
      </sitemap>
      <sitemap>
        <loc>${process.env.app_url}/sitemap/post-sitemap.xml</loc>
        <lastmod>2021-02-17T11:39:04+00:00</lastmod>
      </sitemap>
    </sitemapindex>
    `;

class Sitemap extends React.Component {
  static async getInitialProps({ res }) {
    let hotelService = new HotelService();
    let models = await hotelService.all() || [];

    // const request = await fetch(EXTERNAL_DATA_URL);
    // const posts = await request.json();

    res.setHeader('Content-Type', 'text/xml');
    res.write(createSitemap(models));
    res.end();
  }
}

