import { hotelConsts } from '../actions/HotelAction'

const initialState = {
  params: {
    page: 1
  },
  items: [],
  meta: null
}

const HotelReducer = (state = initialState, action) => {
  switch (action.type) {
    case hotelConsts.RESET_ITEMS:
      return initialState;
    case hotelConsts.ITEMS:
      return {
        ...state,
        items: action.items,
        meta: action.meta,
        params: {
          ...state.params,
          page: parseInt(state.params.page) + 1
        }
      }
    default:
      return state;
  }
}

export default HotelReducer;
