import React from 'react';

import HotelService from "~/packages/TravelgoOne/services/HotelService";

const createSitemap = (models) => `<?xml version="1.0" encoding="UTF-8"?>
    <urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
        ${models.map((model, index) => {
            return `
                    <url>
                        <loc>${`${process.env.app_url}/${model.slug.it}`}</loc>
                    </url>
                `;
          })
          .join('')}
    </urlset>
    `;

class Sitemap extends React.Component {
  static async getInitialProps({ res }) {
    let hotelService = new HotelService();
    models = await hotelService.all();

    // const request = await fetch(EXTERNAL_DATA_URL);
    // const posts = await request.json();

    res.setHeader('Content-Type', 'text/xml');
    res.write(createSitemap(models));
    res.end();
  }
}

